INSERT INTO `tr` (`id`, `order_nr`, `fk_inf`, `tr_lang`, `translation`, `example`) VALUES
(224497, 11, 137478, 'uk', 'нехитрий, простий; самобутній', ' wild apple → дика яблуня, кислиця, лісова яблуня | wild boar → зоол. дика свиня, дикий кабан | wild camomile → бот. дика ромашка, аптечна (лікарська) ромашка | wild cat → зоол. дикий кіт; запальна людина; ризикована справа; неофіційний страйк; поїзд, що йде не за розкладом | wild cherry → дика вишня | wild cotton → бот. ваточник | wild hive → дуплянка (колодка з видовбаною діркою для бджіл) | wild leek → бот. дика цибуля, левурда, черемша | wild mine → незареєстрована, по-хижацькому розроблювана шахта | wild oat → бот. вівсюг | wild pear → дика груша | wild pig → зоол. дикий кабан | wild plantain → бот. банан текстильний (прядильний) | wild radish → бот. редька дика (польова) | wild rose → бот. шипшина собача | wild seedlings → бокові пагінці дерева | wild sow → дика свиня | wild strawberry → бот. лісові суниці | wild times → грозові роки; смутні часи | wild turnip → бот. суріпиця | wild vetch → бот. мишачий горошок | W. West → дикий захід (про захід США) | W. West show → вистава циркового типу (з ковбоями, індіанцями тощо) | W. West film (novel) → ковбойський фільм (роман) | to run wild → здичавіти; заростати (бур\'янами тощо); рости без нагляду (про дітей) '),
(224557, 1, 137505, 'uk', 'Уїлл, Вілл ( зменш. від [[diminutive::William]])', ''),
(224558, 1, 137506, 'uk', 'воля, сила волі', ' strong (weak) will → сильна (слабка) воля | lack of will → відсутність волі, безвільність '),
(224559, 2, 137506, 'uk', 'бажання, воля; хотіння; твердий намір', ' at will → за бажанням, як завгодно | against one\'s will → всупереч бажанню | of one\'s own free will → за власним бажанням, з доброї волі | God\'s will → воля Божа | to show good (ill) will → виявити доброзичливість (недоброзичливість) '),
(224560, 3, 137506, 'uk', 'енергія, ентузіазм', ' with a will → енергійно '),
(224561, 4, 137506, 'uk', 'заповіт, духівниця', ' to make (to draw up) one\'s will → скласти заповіт '),
(224562, 5, 137506, 'uk', 'похіть; статевий потяг', ' at will! → військ. одиночний вогонь! (команда) | tenant at will → орендар, що може бути виселений без попередження | where there is a will there is a way → присл. де хотіння, там і вміння; була б охота, а можливість буде '),
(224563, 1, 137507, 'uk', 'заблудлий, заблукалий; що збився з шляху', ''),
(224564, 2, 137507, 'uk', 'розгублений; заплутаний', ''),
(224565, 3, 137507, 'uk', 'проявляти волю (бажання) ; хотіти, бажати, жадати', ' God has willed it so → на те була воля Божа | he who wills success is half-way to it → сильне бажання є запорукою успіху '),
(224566, 4, 137507, 'uk', 'примушувати, змушувати, веліти, наказувати; вселяти, викликати', ' to will oneself to fall asleep → примусити себе заснути '),
(224567, 5, 137507, 'uk', 'заповідати', ''),
(224568, 1, 137508, 'uk', 'допоміжне дієслово для утворення майбутнього часу', ' she will come at four o\'clock → вона прийде о четвертій годині | you will see him soon → ви незабаром побачите його '),
